mapper.select:
SELECT ID, EMAIL, NAME, TYPE, TO_CHAR(regdate, 'yyyy-MM-DD HH24:MI:SS') regdate
FROM MEMBER
WHERE id = #{value}

mapper.update:
UPDATE MEMBER

    NAME = #{name},
    PASSWORD = #{password},
    IMAGE_PATH = #{imagePath}

WHERE ID = #{id}

UPDATE MEMBER
SET status = 'disabled'
WHERE ID = #{id}
